import './Aibody.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

const Aibody = () => {
    const [messages, setMessages] = useState([]); // State to store the list of Q&A pairs
    const [loading, setLoading] = useState(false); // Loading state for API call
    const [inputText, setInputText] = useState(""); // State to handle text input
    const { transcript, listening, resetTranscript } = useSpeechRecognition(); // Speech recognition hooks
  
    useEffect(() => {
        // Retrieve chat history from localStorage when the component mounts
        const savedMessages = JSON.parse(localStorage.getItem('chatHistory'));
        if (savedMessages) {
            setMessages(savedMessages);
        }
    }, []);
  
    useEffect(() => {
        // Save messages to localStorage whenever the messages state changes
        localStorage.setItem('chatHistory', JSON.stringify(messages));
    }, [messages]);

    if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
      return <div>Your browser does not support speech recognition. Please use a modern browser.</div>;
    }
  
    // Function to start listening to speech
    const handleStartListening = () => {
      if (!listening) {
        SpeechRecognition.startListening({ continuous: true }); // Start continuous listening
      }
    };
  
    // Function to stop listening to speech and use the transcript for the API call
    const handleStopListening = async () => {
      if (listening) {
        SpeechRecognition.stopListening(); // Stop listening when the button is clicked
  
        // Check if the transcript is not empty
        if (!transcript.trim()) {
          alert("Please say something before sending."); // Alert if the transcript is empty
          return;
        } else {
          await fetchApiResponse(transcript); // Call the API with transcript
        }
        resetTranscript(); // Reset the transcript after API call
      }
    };
  
    // Function to make the API call
    const fetchApiResponse = async (input) => {
      setLoading(true); // Start loading
  
      try {
        const response = await axios.post(
          'http://localhost:4000/api/v1/search/QueryAnything', // API endpoint
          { input }, // Send the input (voice or text) to the API
          { headers: { 'Content-Type': 'application/json' } }
        );
  
        const aiMessage = response.data.message; // Extract the response message from the API
  
        // Update messages state with the new Q&A
        setMessages((prevMessages) => [
          ...prevMessages,
          {
            question: input, // Save the input as the question
            answer: aiMessage, // Save the AI response as the answer
          },
        ]);
      } catch (error) {
        console.error('Error fetching response from API:', error); // Handle error
        setMessages((prevMessages) => [
          ...prevMessages,
          {
            question: input, // Save the input even on error
            answer: 'Error fetching response. Please try again.', // Display error message
          },
        ]);
      }
  
      setLoading(false); // End loading
    };
  
    // Function to handle text input submission
    const handleTextSubmit = async (e) => {
      e.preventDefault();
      if (inputText.trim()) {
        await fetchApiResponse(inputText); // Call the API with the text input
        setInputText(""); // Reset the text input
        resetTranscript(); // Reset the transcript when submitting the text
      } else {
        alert("Please enter some text before submitting.");
      }
    };
  
    // Function to reset the responses
    const handleResetResponses = () => {
      setMessages([]); // Clear all Q&A pairs
      resetTranscript(); // Reset the transcript
      setInputText(""); // Clear text input
      localStorage.removeItem('chatHistory'); // Clear chat history from localStorage
    };

    const combinedInput = inputText || transcript; // Combine text input and transcribed input

    return (
      <div className='Contain'>
        <h1>Chat AI : Your Personal Voice & Text Assistance</h1>
  
        {/* Voice Control Buttons */}
        <div className='butn' style={{ marginBottom: '20px' }}>
          <button
            className='startbutn'
            onClick={handleStartListening}
            disabled={listening || inputText.trim().length > 0} // Disable when listening or typing
          >
            Start Listening
          </button>
          <button
            className='stopbutn'
            onClick={handleStopListening}
            disabled={!listening}
          >
            Stop Listening
          </button>
          <button
            className='resetbutn'
            onClick={handleResetResponses}
          >
            Reset Responses
          </button>
          {listening && <p>Listening... Please speak now.</p>}
        </div>
  
        {/* Text Input Form */}
        <form onSubmit={handleTextSubmit}>
          <div className='txtrequest'>
            <textarea
              value={combinedInput} // Combined value from both input sources
              onChange={(e) => setInputText(e.target.value)} // Update the text input state
              rows="4"
              cols="50"
              placeholder="Type or speak your query here..."
              readOnly={listening} // Make the textarea read-only when listening
            />
          </div>
          <button type='submit' className='startbutn' disabled={!inputText.trim()}>
            Submit Text
          </button>
        </form>
  
        {/* Loading and Response Display */}
        {loading ? (
          <p>Loading response...</p>
        ) : (
          <div className='txtresponse'>
            <textarea
              value={messages.map((msg, index) => `Question ${index + 1}: ${msg.question}\nAnswer:\n${msg.answer}\n\n`).join('')}
              rows="10"
              cols="50"
              readOnly
              placeholder="AI responses will appear here..."
            />
          </div>
        )}
      </div>
    );
};

export default Aibody;
