import React, { useState } from 'react';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function App() {
  const [messages, setMessages] = useState(''); // State to store the API response
  const [loading, setLoading] = useState(false); // Loading state for API call
  const [finalTranscript, setFinalTranscript] = useState(''); // State to accumulate transcriptions
  const { transcript, listening, resetTranscript } = useSpeechRecognition(); // Speech recognition hooks

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return <div>Your browser does not support speech recognition. Please use a modern browser.</div>;
  }

  // Function to start listening to speech
  const handleStartListening = () => {
    if (!listening) {
      resetTranscript(); // Reset transcript to capture new voice input
      SpeechRecognition.startListening({ continuous: true }); // Start continuous listening
    }
  };

  // Function to stop listening to speech and call the API
  const handleStopListening = async () => {
    if (listening) {
      SpeechRecognition.stopListening(); // Stop listening when the button is clicked
      setFinalTranscript((prev) => prev + ' ' + transcript); // Append the current transcript to the final transcript
      await handleSendMessage(transcript); // Call the API after stopping
    }
  };

  // Function to send the transcribed message to the API
  const handleSendMessage = async (transcriptToSend) => {
    if (!transcriptToSend.trim()) {
      alert('Please say something before sending.'); // Alert if the transcript is empty
      return;
    }

    setLoading(true); // Start loading

    try {
      const response = await axios.post(
        'http://localhost:4000/api/v1/search/QueryAnything', // API endpoint
        {
          input: transcriptToSend, // Send the accumulated transcribed message as input
        },
        {
          headers: {
            'Content-Type': 'application/json', // Set headers
          }
        }
      );

      // Extract the response message from the API
      const aiMessage = response.data.message;
      setMessages(aiMessage); // Update messages state with API response
    } catch (error) {
      console.error('Error fetching response from API:', error); // Handle error
      setMessages('Error fetching response. Please try again.'); // Display error message
    }

    setLoading(false); // End loading
  };

  return (
    <div style={{ padding: '20px', maxWidth: '600px', margin: 'auto' }}>
      <h1>Chat with AI using Voice</h1>
      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={handleStartListening}
          style={{ padding: '10px', fontSize: '16px', marginRight: '10px' }}
          disabled={listening} // Disable if already listening
        >
          Start Listening
        </button>
        <button
          onClick={handleStopListening}
          style={{ padding: '10px', fontSize: '16px' }}
          disabled={!listening} // Disable if not listening
        >
          Stop Listening and Send
        </button>
        {listening && <p>Listening... Please speak now.</p>}
      </div>
      <div style={{ marginBottom: '20px' }}>
        <textarea
          value={finalTranscript + ' ' + transcript} // Show the accumulated transcript + current transcript
          rows="4"
          cols="50"
          readOnly
          placeholder="Your transcribed text will appear here..."
          style={{ width: '100%', marginBottom: '10px' }}
        />
      </div>
      {loading ? <p>Loading response...</p> : (
        <div style={{ marginTop: '20px' }}>
          <textarea
            value={messages}
            rows="4"
            cols="50"
            readOnly
            placeholder="AI response will appear here..."
            style={{ width: '100%' }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
























