import React, { useState } from 'react';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';

function App() {

  const [messages, setMessages] = useState([]); // To store messages
  const [loading, setLoading] = useState(false); // Loading state for API call
  const { transcript, listening, resetTranscript } = useSpeechRecognition(); // Speech recognition hooks

  if (!SpeechRecognition.browserSupportsSpeechRecognition()) {
    return <div>Your browser does not support speech recognition. Please use a modern browser.</div>;
  }

  const handleMicClick = () => {
    if (listening) {
      SpeechRecognition.stopListening(); // Stop listening when button is clicked again
      handleSendMessage(transcript); // Send the message to OpenAI API
      resetTranscript(); // Reset transcript after sending
    } else {
      SpeechRecognition.startListening(); // Start listening when button is clicked
    }
  };

  const handleSendMessage = async (message) => {
    setLoading(true); // Start loading

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [{ role: 'user', content: message }],
        },
        {
          headers: {
            Authorization: `Bearer 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`, // Ensure API key is set in .env file
          },
        }
      );

      const aiMessage = response.data.choices[0].message.content; // Get the AI response
      setMessages((prevMessages) => [...prevMessages, { role: 'user', content: message }, { role: 'assistant', content: aiMessage }]); // Update messages state
    } catch (error) {
      console.error('Error fetching response from OpenAI:', error); // Handle error
    }

    setLoading(false); // End loading
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>Chat with OpenAI using Voice</h1>
      <div style={{ marginBottom: '20px' }}>
        <button onClick={handleMicClick}>
          {listening ? 'Stop Listening' : 'Start Listening'}
        </button>
        {listening && <p>Listening...</p>}
      </div>
      {loading && <p>Loading response...</p>}
      <div style={{ marginTop: '20px' }}>
        {messages.map((msg, index) => (
          <div key={index} style={{ margin: '10px 0' }}>
            <strong>{msg.role === 'user' ? 'User' : 'AI'}:</strong> {msg.content}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
